"use client";
import { useDeckController } from "@/components/ui/slide-cn/use-deck-controller";
import { useKeyboardNavigation } from "@/components/ui/slide-cn/use-keyboard-navigation";
import React from "react";
import { AnimatePresence } from "motion/react";
import { SlideNav } from "@/components/ui/slide-cn/slide-nav";

const DeckContext = React.createContext<ReturnType<
	typeof useDeckController
> | null>(null);

export function Deck({ children }: { children: React.ReactNode }) {
	const slides = React.Children.toArray(children);
	const deck = useDeckController(slides.length);
	useKeyboardNavigation({
		onNext: deck.next,
		onPrev: deck.prev
	});


	return (
		<DeckContext.Provider
			value={deck}
		>
			<div className="absolute inset-0 overflow-hidden touch-none">

				<SlideNav />
				<div className="absolute inset-0">

					<AnimatePresence mode="wait">
						{slides[deck.index]}
					</AnimatePresence>
				</div>

			</div>

		</DeckContext.Provider>
	);
}

export function useDeck() {
	const ctx = React.useContext(DeckContext);
	if (!ctx) throw new Error("useDeck must be used inside Deck");
	return ctx;
}

"use client";
import React from "react";
import { motion } from "motion/react";
import { useDeck } from "@/components/ui/slide-cn/deck";

type SlideProps = {
	children: React.ReactNode;
};

export function Slide({ children }: SlideProps) {
	const deck = useDeck();
	return (
		<motion.div
			data-slide
			className="absolute inset-0"
			exit={{ opacity: 0 }}
			drag="x"
			dragConstraints={{ left: 0, right: 0 }}
			dragElastic={0.25}
			dragMomentum={false}
			whileDrag={{ scale: 1 }}
			onDragEnd={(event, info) => {
				const swipeDistance = info.offset.x;
				const swipeVelocity = info.velocity.x;

				const DISTANCE_THRESHOLD = 60;
				const VELOCITY_THRESHOLD = 600;

				const isSwipeRight =
					swipeDistance > DISTANCE_THRESHOLD ||
					swipeVelocity > VELOCITY_THRESHOLD;

				const isSwipeLeft =
					swipeDistance < -DISTANCE_THRESHOLD ||
					swipeVelocity < -VELOCITY_THRESHOLD;

				// Extra safety: ignore mostly-vertical gestures
				if (Math.abs(info.offset.y) > Math.abs(info.offset.x)) {
					return;
				}

				if (isSwipeRight) {
					deck.prev();
				} else if (isSwipeLeft) {
					deck.next();
				}
			}}
		>
			{children}
		</motion.div>
	);
}

"use client";

import { Button } from "@/components/ui/button";
import { useDeck } from "@/components/ui/slide-cn/deck";

export function SlideNav() {
	const deck = useDeck();

	return (
		<>
			{/* LEFT ZONE */}
			<div className="hidden md:block pointer-events-none absolute inset-y-0 left-0 w-24">
				<div className="group pointer-events-auto h-full w-full flex items-center">
					<Button
						type="button"
						onClick={deck.prev}
						className="
							ml-4
							opacity-0
							transition-opacity
							group-hover:opacity-100"
					>
						Prev
					</Button>
				</div>
			</div>

			{/* RIGHT ZONE */}
			<div className="hidden md:block pointer-events-none absolute inset-y-0 right-0 w-24">
				<div className="group pointer-events-auto h-full w-full flex items-center justify-end">
					<Button
						type="button"
						onClick={deck.next}
						className="
              mr-4
              opacity-0
              transition-opacity
              group-hover:opacity-100
            "
					>
						Next
					</Button>
				</div>
			</div>
		</>
	);
}

"use client";
import { useCallback, useState } from 'react';

export function useDeckController(slideCount: number) {
	const [index, setIndex] = useState(0);

	const clamp = useCallback(
		(slideIndex: number) => {
			if (slideIndex < 0) return 0;
			if (slideIndex >= slideCount) return slideCount - 1;
			return slideIndex;
		},
		[slideCount]
	);

	const next = useCallback(() => {
		setIndex((i) => clamp(i + 1));
	}, [clamp]);

	const prev = useCallback(() => {
		setIndex((i) => clamp(i - 1));
	}, [clamp]);

	const goTo = useCallback(
		(target: number) => {
			setIndex(clamp(target));
		},
		[clamp]
	);

	return {
		index,
		next,
		prev,
		goTo,
	};
}

import { useEffect } from "react";

type KeyboardNavigationOptions = {
	onNext: () => void;
	onPrev: () => void;
	disabled?: boolean;
};

export function useKeyboardNavigation({
	onNext,
	onPrev,
	disabled = false,
}: KeyboardNavigationOptions) {
	useEffect(() => {
		if (disabled) return;

		const handler = (e: KeyboardEvent) => {
			// Ignore key repeat (holding down key)
			if (e.repeat) return;

			if (e.key === "ArrowRight" || e.key === " ") {
				e.preventDefault();
				onNext();
			}

			if (e.key === "ArrowLeft") {
				e.preventDefault();
				onPrev();
			}
		};

		window.addEventListener("keydown", handler);
		return () => {
			window.removeEventListener("keydown", handler);
		};
	}, [onNext, onPrev, disabled]);
}
